import { useState } from "react";
import { HiOutlineMenu } from "react-icons/hi";
import { IoMdHome } from "react-icons/io";
import classes from "./MainNavigation.module.css";
import NavigationList from "./NavigationList";

const MainNavigation = () => {
  const [showMenu, setShowMenu] = useState(false);
  const menuToggleHandler = () => {
    setShowMenu((prevState) => !prevState);
  };

  const wideNavLinks = [
    { id: "home", to: "home", title: "Home" },
    { id: "about", to: "about", title: "About Me" },
    { id: "projects", to: "projects", title: "Projects" },
    { id: "contact", to: "contact", title: "Contact" },
  ];

  const mobileNavLinks = [
    { id: "home", to: "home", title: <IoMdHome/> },
    { id: "about", to: "about", title: "About Me" },
    { id: "projects", to: "projects", title: "Projects" },
    { id: "contact", to: "contact", title: "Contact" },
  ];

  return (
    <nav className={classes.nav}>
      <HiOutlineMenu
        className={classes["hamburger-button"]}
        onClick={menuToggleHandler}
      />
      <NavigationList
        className={classes["wide-navigation"]}
        menu={wideNavLinks}
      />
      {showMenu && (
        <NavigationList
          className={classes["mobile-navigation"]}
          onClick={menuToggleHandler}
          menu={mobileNavLinks}
        />
      )}
    </nav>
  );
};

export default MainNavigation;
