import { useState } from "react";
import { Link } from "react-scroll";
import Hamburger from "./Hamburger";
import classes from "./MainNavigation.module.css";

const MainNavigation = () => {
  const [showMenu, setShowMenu] = useState(false);
  const menuToggleHandler = () => {
    setShowMenu((prevState) => !prevState);
  };

  const menu = (<ul>
    <li>
      <Link
        activeClass={classes.active}
        to="home"
        spy={true}
        smooth={true}
        offset={-300}
        duration={500}
        delay={0}
      >
        Home
      </Link>
    </li>
    <li>
      <Link
        activeClass={classes.active}
        to="about"
        spy={true}
        smooth={true}
        offset={-300}
        duration={500}
        delay={0}
      >
        About Me
      </Link>
    </li>
    <li>
      <Link
        activeClass={classes.active}
        to="projects"
        spy={true}
        smooth={true}
        offset={-300}
        duration={500}
        delay={0}
      >
        Recent Projects
      </Link>
    </li>
    <li>
      <Link
        activeClass={classes.active}
        to="contact"
        spy={true}
        smooth={true}
        offset={-300}
        duration={300}
        delay={0}
      >
        Contact
      </Link>
    </li>
  </ul>)

  return (
    <nav className={classes.nav}>
      <Hamburger onClick={menuToggleHandler} />
      {showMenu && menu}
    </nav>
  );
};

export default MainNavigation;
