import { useState } from "react";
import { HiOutlineMenu } from "react-icons/hi";
import { CgClose } from "react-icons/cg";
import { AiFillHome } from "react-icons/ai";
import { MdOutlineAccountCircle } from "react-icons/md";
import { GoFileCode } from "react-icons/go";
import { AiOutlinePhone } from "react-icons/ai";
import classes from "./MainNavigation.module.css";
import NavigationList from "./NavigationList";

const MainNavigation = () => {
  const [showMenu, setShowMenu] = useState(false);
  const menuToggleHandler = () => {
    setShowMenu((prevState) => !prevState);
  };

  const wideNavLinks = [
    { id: "projects", to: "projects", title: "Projects", offset: -500 },
    { id: "contact", to: "contact", title: "Contact", offset: -500 },
  ];

  const mobileNavLinks = [
    {
      id: "home",
      to: "home",
      title: <AiFillHome className={classes["nav-icon"]} />,
      offset: -350,
    },
    {
      id: "projects",
      to: "projects",
      title: <GoFileCode className={classes["nav-icon"]} />,
      offset: -350,
    },
    {
      id: "contact",
      to: "contact",
      title: <AiOutlinePhone className={classes["nav-icon"]} />,
      offset: -350,
    },
  ];

  return (
    <nav className={classes.nav}>
      {!showMenu ? (
        <div className={classes.bar}>
          <HiOutlineMenu
            className={classes["hamburger-button"]}
            onClick={menuToggleHandler}
          />
        </div>
      ) : (
        <div className={classes.bar}>
          <CgClose
            className={classes["hamburger-button"]}
            onClick={menuToggleHandler}
          />
          <NavigationList
            className={classes["mobile-navigation"]}
            menu={mobileNavLinks}
          />
        </div>
      )}
      <NavigationList
        className={classes["wide-navigation"]}
        menu={wideNavLinks}
      />
    </nav>
  );
};

export default MainNavigation;
